private final ObservableList<ICard> deckContains20; private final ObservableList<ICard> deckNotContains20;
public Say20OperatorTest() { deckContains20 = FXCollections.observableArrayList( new HungarianCard( HungarianCardRank.KIRALY, HungarianCardSuit.PIROS), new HungarianCard( HungarianCardRank.FELSO, HungarianCardSuit.PIROS), new HungarianCard( HungarianCardRank.ASZ, HungarianCardSuit.ZOLD), new HungarianCard( HungarianCardRank.TIZ, HungarianCardSuit.MAKK), new HungarianCard( HungarianCardRank.ALSO, HungarianCardSuit.TOK));
deckNotContains20 = FXCollections.observableArrayList( new HungarianCard( HungarianCardRank.KIRALY, HungarianCardSuit.PIROS), new HungarianCard( HungarianCardRank.FELSO, HungarianCardSuit.ZOLD), new HungarianCard( HungarianCardRank.ASZ, HungarianCardSuit.ZOLD), new HungarianCard( HungarianCardRank.TIZ, HungarianCardSuit.MAKK), new HungarianCard( HungarianCardRank.ALSO, HungarianCardSuit.TOK)); }


@Before
public void setUp() {
    gameMatchMock = Mockito.mock(GameMatch.class);
    currentPlayer = Mockito.mock(Player.class);
    nextPlayer = Mockito.mock(Player.class);
}

@Test
public void applicableTest() {
    when(currentPlayer.getCards()).thenReturn(deckContains20);
    when(gameMatchMock.getCurrentPlayer()).thenReturn(currentPlayer);
    Say20Operator say20Operator = new Say20Operator(currentPlayer);
    assertTrue(say20Operator.isApplicable(gameMatchMock));
}


@Test
public void notApplicableTest() {
    when(currentPlayer.getCards()).thenReturn(deckNotContains20);
    when(gameMatchMock.getCurrentPlayer()).thenReturn(currentPlayer);
    Say20Operator say20Operator = new Say20Operator(currentPlayer);
    assertFalse(say20Operator.isApplicable(gameMatchMock));
}


@Test
public void applyTest() {
    when(currentPlayer.getCards()).thenReturn(deckContains40);
    when(gameMatchMock.getCurrentPlayer()).thenReturn(currentPlayer);
    Say40Operator say40Operator = new Say40Operator(currentPlayer);
    say40Operator.apply(gameMatchMock);
    verify(currentPlayer).setSaid40(eq(true));
    verify(currentPlayer).addScore(eq(40));
}
